import React, { useState } from 'react';
import AD from './Ad';

function AdPage() {
  const [showAD, setShowAD] = useState(true);

  const handleToggleClick = () => {
    setShowAD(!showAD);
  };

  return (
    <div>
      <button type="button" onClick={handleToggleClick}>
        {showAD ? '닫기' : '열기'}
      </button>
      <AD show={showAD}>hh</AD>
    </div>
  );
}

export default AdPage;
